  return (
    <div>
      <BrowserRouter>
        <div className="header2">
          <div className="header1">my portfolio</div>
        </div>
        <div className="comp">
          <Link to="/">Home</Link>
          <Routes>
            <Route
              path="/"
              element={
                <div className="whole">
                  <div className="headers">
                    <h1 className="header">TO DO LIST</h1>
                  </div>
                  <input
                    onKeyDown={addwhenenter}
                    type="text"
                    //value attribute of input tag in react works only with usestate or else they stay static with defaulot value given
                    value={task}
                    onChange={(event) => {
                      settask(event.target.value);
                    }}
                    className="input-task"
                  />
                  <button onClick={addtask}>add</button>
                  <div className="todo-cont">
                    <ul className="list">
                      {todo
                        ? todo.map((dt) => {
                            return (
                              <div className="todo-container" key={dt.id}>
                                <div className="todo">
                                  <div className="task">
                                    <input type="checkbox" />
                                    {dt.sub}
                                  </div>
                                  <button
                                    onClick={() => deltask(dt.id)}
                                    className="rm-btn"
                                  >
                                    remove
                                  </button>
                                </div>
                              </div>
                            );
                          })
                        : "loading"}
                    </ul>
                  </div>
                </div>
              }
            />
          </Routes>
          <div className="FAQ">
            <Link to="/faq">
              <h1>FAQ</h1>
            </Link>
            <Link to="/rqfaq">
              <h1>RQFAQ</h1>
            </Link>
            <Routes>
              <Route
                path="/faq"
                element={
                  // {data.map((ar) => {
                  //     return <Ques title={ar.title} key={ar.id} info={ar.info} />;
                  //   })}
                  <div>
                    <Faq />
                  </div>
                }
                exact
              />
              <Route
                path="/rqfaq"
                element={
                  // {data.map((ar) => {
                  //     return <Ques title={ar.title} key={ar.id} info={ar.info} />;
                  //   })}
                  <div>
                    <RqFaq />
                  </div>
                }
                exact
              />
            </Routes>
          </div>
          <div className="footer">
            <p>About me , </p>I am Vishal, studying at iiitdm kancheepuram
          </div>
        </div>
      </BrowserRouter>
    </div>
  );
};
