import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

let a;

export const Signup = () => {
  const handleSubmit = (event) => {
    //to stop loading after form is submitted the backend will be processed but page will not loaded so it will not be redirected
    //event.preventDefault();
    axios
      .post("http://localhost:5000", {
        username: username,
        password: password,
      })
      //gives an error if setResponse(res)
      .then((res) => {
        console.log(res);
        setResponse(res.status);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const [username, setUserName] = useState();
  const [response, setResponse] = useState("");
  const [password, setPassword] = useState();
  a = username;
  return (
    <div>
      <div className="login-page">
        <div className="login-box">
          <form onSubmit={handleSubmit}>
            <label className="username">
              Username:
              <input
                type="text"
                name="username"
                placeholder="Enter username"
                onChange={(e) => setUserName(e.target.value)}
              />
            </label>
            <br />
            <label className="username">
              Password:
              <input
                type="password"
                name="password"
                placeholder="Enter password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </label>
            <br />
            <Link to="/user">
              <button type="submit" onClick={handleSubmit} className="lgn-btn">
                signup
              </button>
            </Link>
            {response ? <div id="response">{response}</div> : null}
          </form>
        </div>
      </div>
    </div>
  );
};

